  return (
    <header className={classes.header}>
      <h1>Redux Auth</h1>
    {isAuthorized && (
      <nav>
        <ul>
          <li>
            <a href='/'>My Products</a>
          </li>
          <li>
            <a href='/'>My Sales</a>
          </li>
          <li>
            <button onClick={()=> dispatch({type:authActionTpyesv.LOG_OUT})}>Logout</button>
          </li>
        </ul>
      </nav>
    )}  
    </header>
  );
};

export default Header;
