import { useSelector } from "react-redux";
import { calCulatorActionTypes, store } from "../store";
import classes from "./Counter.module.css";

const Calculator = () => {
  const { result } = store.getState();

   useSelector((state)=> state)

  console.log(result);

  const addHandler = () => {
    store.dispatch({ type: calCulatorActionTypes.ADD, payload: 5 });
  };

  const divideHandler = () => {
    store.dispatch({ type: calCulatorActionTypes.DIVIDE, payload: 4 });
  };

  const subtractHandler = () => {
    store.dispatch({ type: calCulatorActionTypes.SUBTRACT, payload: 10 });
  };

  const multilyHandler = () => {
    store.dispatch({ type: calCulatorActionTypes.MULTIPLY, payload: 2 });
  };

  return (
    <main className={classes.counter}>
      <h1>Redux Counter</h1>
      <div className={classes.value}>Result: {result}</div>
      <button onClick={addHandler}>+5</button>
      <button onClick={divideHandler}>/4</button>
      <button onClick={subtractHandler}>-10</button>
      <button onClick={multilyHandler}>*2</button>
    </main>
  );
};

export default Calculator;
